"use strict";

var through = require("through");
var autoprefixer = require("autoprefixer");

module.exports = function (fileName, options) {

    options = options || {};
    if (typeof(options['auto-inject']) == 'undefined') {
        options['auto-inject'] = true;
    }
    if (typeof(options['auto-prefix']) == 'undefined') {
        options['auto-prefix'] = true;
    }

    if (!/\.css$/i.test(fileName)) {
        return through();
    }

    var inputString = "";

    return through(
        function (chunk) {
            inputString += chunk;
        },
        function () {
            var stringifiedCss = JSON.stringify(inputString);

            var css = options['auto-prefix']
              ? autoprefixer.process(stringifiedCss).css
              : stringifiedCss;

            var moduleBody = options['auto-inject']
              ? "var css = " + css + "; (require("+JSON.stringify(__dirname)+"))(css); module.exports = css;"
              : "module.exports = " + css;

            this.queue(moduleBody);
            this.queue(null);
        }
    );
};
